                  test_mem_ctrl.error_flg <= 1'b1;
               end
            end
		    end
		`endif

		`ifdef COL_WIDTH_LT_10
		    else if ((fifo_out_cmd == 4'b0100) || (fifo_out_cmd == 4'b0011)) begin
             if (burst_cycle == orig_mem_burst_cnt) begin
