//    if (wr_cmd_clkcnt > 2000) begin
//        $display ("MON ERROR: At time %0t, more than 2000 clocks elapsed without a write cycle.  Ending test.\n", $time);
//        test_mem_ctrl.error_flg <= 1'b1;
//        mon_endoftest     = 1;
//	//$finish;
//    end
//end
//
//always @ (rd_cmd_clkcnt) begin
