                test_mem_ctrl.error_flg <= 1'b1;
            end
             `ifdef NO_DEBUG
             `else
                else if (dataout === rd_data_compare)
        	       $display("DATA MATCH!: At time %0t, data output to FPGA is %h, Expected data is %h\n",$time, dataout, rd_data_compare);
             `endif
         `endif
    
