// Flag an error if init done is ever asserted without the init_start 
// being active

reg  init_start_d;

always @ (posedge clk) begin
   if (init_done && !init_start_d) begin
        $display ("MON ERROR: At time %0t, init_done was asserted without a valid init_start request\n", $time);
        test_mem_ctrl.error_flg <= 1'b1;
   end
end

reg  [5:0] otf_fifo[0:7];
reg  [2:0] otf_wr_addr;
reg  [2:0] otf_rd_addr;
reg  [5:0] otf_fifo_out;
wire       otf_bl;
wire [4:0] otf_burst_cnt;
wire       otf_fifo_rd;//the otf_fifo read point is increased on the begining of each RD/WR
reg  [4:0] mem_burst_cnt_dup;
reg  [4:0] orig_mem_burst_cnt_dup;

always @ (posedge mem_clk or negedge rst_n) begin
   if (!rst_n) begin
      otf_wr_addr <= 3'd0;
      mem_burst_cnt_dup <= 5'd0;
   end
   else begin
      if (fifo_rd) begin
         if (mem_rd_cmd || mem_wr_cmd)
           mem_burst_cnt_dup  =  fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4];
      end
      orig_mem_burst_cnt_dup  = fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4];
       
      if (mem_rd_cmd || mem_wr_cmd) begin
         if (mem_burst_cnt_dup != 5'b0) begin
           mem_burst_cnt_dup = mem_burst_cnt_dup - 1;
       end
       else begin
           mem_burst_cnt_dup = 0;
       end
     end

      if (burst_len == 2'b01) begin // OTF
         if ((mem_rd_cmd || mem_wr_cmd)&& (mem_burst_cnt_dup == orig_mem_burst_cnt_dup-1)) begin 
            otf_wr_addr <= otf_wr_addr + 1;
        
            case (otf_wr_addr)
               3'd0 : otf_fifo[0] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd1 : otf_fifo[1] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd2 : otf_fifo[2] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd3 : otf_fifo[3] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd4 : otf_fifo[4] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd5 : otf_fifo[5] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd6 : otf_fifo[6] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
               3'd7 : otf_fifo[7] <= {fifo_out[`ADDR_WIDTH+8:`ADDR_WIDTH+4],ddr_ad[12]};
            endcase
         end //if (mem_rd_cmd || mem_wr_cmd)
      end // OTF
   end // else
end // always @(posedge mem_clk)

// for the ddr_dqs_modified is #1 delayed from dqs, use negedge to sample it.
always @(negedge mem_clk or negedge rst_n) begin
   if (!rst_n) begin
      dqs_reg1 <= 0;
      dqs_reg2 <= 0;
      dqs_reg3 <= 0;
      dqs_reg4 <= 0;
      dqs_reg5 <= 0;
      dqs_reg6 <= 0;
      dqs_reg7 <= 0;
   end
   else begin
      dqs_reg1 <= ddr_dqs_original;
      dqs_reg2 <= dqs_reg1;
      dqs_reg3 <= dqs_reg2;
      dqs_reg4 <= dqs_reg3;
      dqs_reg5 <= dqs_reg4;
      dqs_reg6 <= dqs_reg5;
      dqs_reg7 <= dqs_reg6;
   end // else
end //always @(negedge mem_clk)

